<?phpinclude_once('model/startup.php');include_once('model/M_Users.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Менеджеры.$mUsers = M_Users::Instance();// Очистка старых сессий.$mUsers->ClearSessions();// Текущий пользователь.$user = $mUsers->Get();// Если пользователь не зарегистрирован - отправляем на страницу регистрации.if ($user == null){	header("Location: login.php");	die();}// Может ли пользователь смотерть зарплату?if (!$mUsers->Can('VIEW_SALARY') && !$mUsers->Can('CHANGE_SALARY')){	die('Отказано в доступе');}if ($mUsers->Can('CHANGE_SALARY')){	$message = "Форма для редактирования данных об окладе";	$right = '';}else{	$message = "Только просмотр данных об окладе";	$right = 'disabled';}// Кодировка.header('Content-type: text/html; charset=utf-8');?><!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html><head>	<title>Веб-Гуру</title>	<meta content="text/html; charset=utf-8" http-equiv="content-type">		<link rel="stylesheet" type="text/css" media="screen" href="theme/style.css" /> </head><body>	<h1>Оклад сотрудников</h1>	<a href="index.php">Главная</a>	<br>	<fieldset <?php echo $right?>>   <legend><?php echo $message?></legend>     <input type="text" value ="14 000"> Иванов<br>     <input type="text" value ="28 000"> Петров<br>     <input type="text" value ="96 000"> Замуруев<br>	 <input type="text"  value ="48 000"> Семенова<br>      <p><input type="submit"></p>  </fieldset>  </body></html>