<?phprequire_once 'settings.php';function __autoload($classname){	$classname=strtolower($classname);	$dir=mb_substr($classname,0,1);	if(is_file("{$dir}/{$classname}.php")){		require_once("{$dir}/{$classname}.php");	}	else{		die('Класс не найден '.$classname);	}}$action='action_index';$cont='c_index';if(isset($_GET['act'])){	$action = 'action_'.$_GET['act'];}if(isset($_GET['c'])){	$cont='c_'.ucfirst($_GET['c']);}if(!class_exists($cont)){	header("{$_SERVER['SERVER_PROTOCOL']} 404 Not Found");	die();}$controller = $cont::Instance();$controller->Request($action);