<?phpinclude_once('model/M_MYSQL.php');include_once('model/const.php');function text_add($title,$text){	$articles = new Article();	$text = $articles-> add_article($title,$text);	//var_dump($text);	return $text;}function text_get(){	$articles = new Article();	$text = $articles-> view_article();	return $text;}function text_get_one($id){	$article = new Article();	$text = $article-> view_one_article($id);		return $text;}function text_set($id,$title,$text){	$articles = new Article();	$text = $articles-> edit_article($id,$title,$text);	return $text;	}function text_delete($id){	$articles = new Article();	$text = $articles-> delete_article($id);	//var_dump($text);	return $text;}function clear_article($text){  $text  = preg_replace('| +|', ' ', $text);  $order = array("\r\n", "\n", "\r","&amp;","&quot;","&#039;","&lt;","&gt;","\\","quot;");  $text  =str_replace($order,' ', $text);  //$text = htmlspecialchars($text);  return $text;  }function maxsite_str_word($text, $counttext = 10, $sep = ' ') {    $words = explode($sep, $text);    if ( count($words) > $counttext )        $text = join($sep, array_slice($words, 0, $counttext));    return $text;}