<?phpfunction __autoload($classname){	require_once("model/$classname.php");	}// Менеджеры.$mUsers = M_Users::Instance();// Очистка старых сессий.$mUsers->ClearSessions();// Текущий пользователь.$user = $mUsers->Get();//Выводит на экран кто из пользователей онлайнif (!$mUsers->IsOnline($user)) {	if ($user != null)		echo $user['login']."- online";}?><!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html><head>	<title>Веб-Гуру</title>	<meta content="text/html; charset=utf-8" http-equiv="content-type">		<link rel="stylesheet" type="text/css" media="screen" href="theme/style.css" /> </head><body>	<h1>Главная</h1>	<ul>		<li><a href="login.php">Вход/Выход</a></li>		<li><a href="contact.php">Контактная информация сотрудников</a></li>		<li><a href="salary.php">Оклад сотрудников</a></li>	</ul></body></html>